var commands = module.exports,
    Q = require('q'),
    EventEmitter = require('events').EventEmitter;

commands.buildCommand1 = function (data, callback) {
    callback(null, 'callback: Moi');
};

commands.buildCommand2 = function (data) {
    return Q.resolve('promise: Tervetuloa');
};

commands.buildCommand3 = function (data) {
    var eventEmitter = new EventEmitter();
    setTimeout(function () {
       eventEmitter.emit('end', 'event: Heippa');
    });
    return eventEmitter;
};

commands.options = {
    type: 'app',
    timing: 'before'
};